package io.github.knifeofdreams.adventofcode.day6;

import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.api.Test;

class DeviceTest {

  @Test
  public void charactersProcessedBeforeFirstPacket1() {
    var datastreamBuffer = "mjqjpqmgbljsphdztnvjfqwrcgsmlb";
    var device = new Device();
    assertEquals(7, device.charsBeforeMarker(datastreamBuffer));
  }

  @Test
  public void charactersProcessedBeforeFirstPacket2() {
    var datastreamBuffer = "bvwbjplbgvbhsrlpgdmjqwftvncz";
    var device = new Device();
    assertEquals(5, device.charsBeforeMarker(datastreamBuffer));
  }

  @Test
  public void charactersProcessedBeforeFirstPacket3() {
    var datastreamBuffer = "nppdvjthqldpwncqszvftbrmjlhg";
    var device = new Device();
    assertEquals(6, device.charsBeforeMarker(datastreamBuffer));
  }

  @Test
  public void charactersProcessedBeforeFirstPacket4() {
    var datastreamBuffer = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg";
    var device = new Device();
    assertEquals(10, device.charsBeforeMarker(datastreamBuffer));
  }

  @Test
  public void charactersProcessedBeforeFirstPacket5() {
    var datastreamBuffer = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw";
    var device = new Device();
    assertEquals(11, device.charsBeforeMarker(datastreamBuffer));
  }

  @Test
  public void charactersProcessedBeforeFirstPacketForMyInput() {
    var datastreamBuffer = "";
    var device = new Device();
    assertEquals(0, device.charsBeforeMarker(datastreamBuffer));
  }
}