package io.github.knifeofdreams.adventofcode.day6;

import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.api.Test;

class DeviceTest {

  private static final int FIRST_PACKET_LENGTH = 4;
  private static final int SECOND_PACKET_LENGTH = 14;
  private Device device = new Device();

  @Test
  public void charactersProcessedBeforeFirstPacket1() {
    assertEquals(7, device.charsBeforeMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb", FIRST_PACKET_LENGTH));
  }

  @Test
  public void charactersProcessedBeforeFirstPacket2() {
    assertEquals(5, device.charsBeforeMarker("bvwbjplbgvbhsrlpgdmjqwftvncz", FIRST_PACKET_LENGTH));
  }

  @Test
  public void charactersProcessedBeforeFirstPacket3() {
    assertEquals(6, device.charsBeforeMarker("nppdvjthqldpwncqszvftbrmjlhg", FIRST_PACKET_LENGTH));
  }

  @Test
  public void charactersProcessedBeforeFirstPacket4() {
    assertEquals(10, device.charsBeforeMarker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", FIRST_PACKET_LENGTH));
  }

  @Test
  public void charactersProcessedBeforeFirstPacket5() {
    assertEquals(11, device.charsBeforeMarker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", FIRST_PACKET_LENGTH));
  }

  @Test
  public void charactersProcessedBeforeFirstPacketForMyInput() {
    var datastreamBuffer = "";
    assertEquals(1658, device.charsBeforeMarker(datastreamBuffer, FIRST_PACKET_LENGTH));
  }

  @Test
  public void charactersProcessedBeforeLongFirstPacketForMyInput() {
    var datastreamBuffer = "";
    assertEquals(2260, device.charsBeforeMarker(datastreamBuffer, SECOND_PACKET_LENGTH));
  }
}